private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    String Accion = TextoMala.getText();
    String tl = "consult('Consultas_Santa.pl')";
  Query q1 = new Query(tl);
  System.out.println(tl +"  "+(q1.hasSolution()?"exito":"falla"));
  
  String t2="malaccion("+Accion+", Nombre)";
  Query q2=new Query(t2);  

   
        if(q2.hasSolution())
        {
            String Resp = q2.oneSolution().get("Nombre").toString();
            jTextArea1.setText(Resp);
        }
}//GEN-LAST:event_jButton1ActionPerformed

